        }
        if ((Fechazodiaco.Month == 8 && Fechazodiaco.Day >= 23) || (Fechazodiaco.Month == 9 && Fechazodiaco.Day <= 22))
        {
            zodiaco = "Virgo";

        }
        if ((Fechazodiaco.Month == 9 && Fechazodiaco.Day >= 23) || (Fechazodiaco.Month == 10 && Fechazodiaco.Day <= 22))
        {
            zodiaco = "Libra";
      
